// script.min.js – Optimized version
document.addEventListener("DOMContentLoaded",function(){var btn=document.querySelector(".cta");btn&&btn.addEventListener("mouseover",function(){this.style.backgroundColor="#e65c00"}),btn&&btn.addEventListener("mouseout",function(){this.style.backgroundColor="#ff6f00"});window.addEventListener("load",function(){console.log("Page fully loaded – ready.")});});

// script.min.js – add at end
document.addEventListener("DOMContentLoaded",function(){
  var yt=document.querySelector(".youtube-lazy");
  if(yt){
    var id=yt.dataset.embed;
    yt.style.backgroundImage='url(https://img.youtube.com/vi/'+id+'/hqdefault.jpg)';
    yt.addEventListener("click",function(){
      var iframe=document.createElement("iframe");
      iframe.setAttribute("frameborder","0");
      iframe.setAttribute("allowfullscreen","");
      iframe.setAttribute("allow","accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture");
      iframe.setAttribute("src","https://www.youtube.com/embed/"+id+"?autoplay=1");
      this.innerHTML="";
      this.appendChild(iframe);
    });
  }
});
